package pack_0131;

public class Training_4_35 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=1;
		while(i<=10) {
			System.out.println(i);
			i++;
		}
		
		int sum = 0;
		i = 1;
		while(i<=10) {
			sum+=i;
			i++;
		}
		System.out.println("1부터 10까지의 합은 "+sum);
	}

}
